    def girisButton(self):
        sifre = self.sifre.text()
        print(sifre)

        conn = pyodbc.connect('Driver={SQL Server};'
                              'Server=DESKTOP-JTG10RJ\SQLEXPRESS;'
                              'Database=Sirket;'
                              'Trusted_Connection=yes;')
        cursor = conn.cursor()
        cursor.execute('SELECT * FROM Sirket.dbo.sirkettalep')
        rows = cursor.fetchall()
        for row in rows:
            print(row.ad, row.soyad)

            self.tumList.addItems([row.ad + row.soyad + row.tarih])

        if sifre == "111":
            self.tumList.setVisible(True)


        elif sifre == "000":
            self.tumList.setVisible(True)

        else:
            msg = QMessageBox()
            msg.setText("Şifre Eşleşmedi")
            x = msg.exec_()

    def pushData(self):

        # printing pressed
        print("pressed")
        isimValue = self.isim.text()
        soyisimValue = self.soyisim.text()
        tarihValue = self.dateEdit.date()
        tarih = tarihValue.toString("yyyy-MM-dd")

        print(isimValue)

        conn = pyodbc.connect('Driver={SQL Server};'
                              'Server=DESKTOP-JTG10RJ\SQLEXPRESS;'
                              'Database=Sirket;'
                              'Trusted_Connection=yes;')
        cursor = conn.cursor()
        cursor.execute("INSERT INTO Sirket.dbo.sirkettalep VALUES (?,?,?,?,?)",
                       (isimValue, soyisimValue, 'False', tarih, 'IK'))

        cursor.execute('SELECT * FROM Sirket.dbo.sirkettalep Where ad=? AND soyad=?', (isimValue, soyisimValue))

        # options = list()
        # for row in cursor:
        # print(row)
        # options.append(row)

        rows = cursor.fetchall()
        for row in rows:
            print(row.ad, row.soyad)

            self.gecmisList.addItems([row.ad + row.soyad + row.tarih])



